import React, {useContext, useState, useRef} from "react";
import { Store } from "./Store";

const ParentForm = (props) => {
    const HOST_API = props.HOST_API
    const formRef = useRef(null);
    const { dispatch, state: { todo } } = useContext(Store);
    const item = todo.parent;
    const [state, setState] = useState(item);

    const onAdd = (event) => {
        event.preventDefault();

        if(state.name === undefined || !/\S/.test(state.name)){
            state.name = undefined;
            return;
        }

        const request = {
            parent: state.name.trim(),
            id: null,
            completed: false
          };
      
      
        fetch(HOST_API + "/todo", {
          method: "POST",
          body: JSON.stringify(request),
          headers: {
            'Content-Type': 'application/json'
          }
        })
          .then(response => response.json())
          .then((todo) => {
            dispatch({ type: "add-item", item: todo });
            setState({ parent: "" });
            formRef.current.reset();
          });
    }

    const onEdit = (event) => {
        event.preventDefault();

        if(state.name === undefined || !/\S/.test(state.name)){
            state.name = undefined;
            return;
        }
    
        const request = {
          name: state.name.trim(),
          id: item.id,
          isCompleted: item.isCompleted
        };
    
    
        fetch(HOST_API + "/todo", {
          method: "PUT",
          body: JSON.stringify(request),
          headers: {
            'Content-Type': 'application/json'
          }
        })
          .then(response => response.json())
          .then((todo) => {
            dispatch({ type: "update-item", item: todo });
            setState({ parent: "" });
            formRef.current.reset();
          });
    }

    return(
        <form className="MyForm" ref={formRef}>
            <input className = "MyInput"
                type="text"
                name="name"
                placeholder="AÃ±adir Campo de Tareas"
                onChange={(event) => {
                    setState({ ...state, parent: event.target.value })
                  }} ></input>
            {item.id && <button className = "MyButton" onClick={onEdit}>Actualizar</button>}
            {!item.id && <button className = "MyButton" onClick={onAdd}>Crear</button>}    
        </form>);
}

export default ParentForm;
