        const request = {
        name: todo.name,
        id: todo.id,
        completed: event.target.checked
        };
        fetch(HOST_API + "/todo", {
        method: "PUT",
        body: JSON.stringify(request),
        headers: {
            'Content-Type': 'application/json'
        }
        })
